CREATE DATABASE banka_sistemi;
use banka_sistemi;

CREATE TABLE hesap (HESAP_NUMARASI int NOT NULL AUTO_INCREMENT, TC varchar(11) NOT NULL, AD_SOYAD varchar(100) NOT NULL, PAROLA varchar(50) NOT NULL, HESAP_TURU varchar(50) NOT NULL, HESAP_ACMA_TARIHI date NOT NULL, KILITLI bit NOT NULL, PARA_MIKTARI double, PRIMARY KEY (HESAP_NUMARASI)) ENGINE=InnoDB DEFAULT CHARSET=utf8;

CREATE TABLE hesap_hareketleri (ID int NOT NULL AUTO_INCREMENT, HESAP_NUMARASI int NOT NULL, TARIH date NOT NULL, YATIRILAN_CEKILEN_MIKTAR int NOT NULL, FIRMA varchar(150), PRIMARY KEY (ID)) ENGINE=InnoDB DEFAULT CHARSET=utf8;

